css:
.quotation-table {
  width: 100%;
  border-collapse: collapse;
  table-layout: fixed;
  font-family: Arial, sans-serif;
}

.quotation-table th,
.quotation-table td {
  padding: 8px;
  border: 1px solid #ccc;
  vertical-align: middle;
  word-wrap: break-word;
  text-align: center;
}

.unit-cell,
.rate-cell {
  text-align: center;
  white-space: nowrap;
}

.g-total-row td {
  font-weight: bold;
  text-align: right;
}

.additional-heading td {
  padding: 10px;
  background-color: #9e8976;
  color: white;
  font-weight: bold;
  text-align: center;
  font-size: 11pt;
}

.additional-gap td {
  height: 60px;
  padding: 0;
  border: none !important;
}

.quotation-table td.item-group-cell {
  background-color: #9e8976;
  font-weight: bold;
  color: white;
}
