employee = frappe.get_doc("Employee", {"user_id": frappe.session.user})
companies = frappe.get_all("Company")

for company in companies:
    if company.name == employee.company:
        plan = frappe.db.get_value("Company", {'name': employee.company}, 'custom_current_plan')
        if plan == "Starter":
            frappe.throw("Can't add another user. Please upgrade your plan")
